import React, { Fragment, useEffect, useState } from "react";
import { getUserBooking } from "../../api-helpers/api-helpers";
import { Box, List, ListItem, ListItemText, Typography } from "@mui/material";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import bookings from '../../backend/models/Bookings';
import DeleteIcon from '@mui/icons-material/Delete';

const UserProfile = () => {
  const [bookings, setBookings] = useState();
  useEffect(() => {
    getUserBooking().then((res) =>
      setBookings(res.bookings).catch((err) => console.log(err))
    );
  }, []);
  console.log(bookings);
  return (
    <Box width={"100%"} display="flex">
      {bookings && bookings.length > 0 && (
        <Fragment>
          {" "}
          <Box
            width="30%"
            flexDireciton={"column"}
            justifyContent="center"
            alignItems={"center"}
            padding={3}
          >
            <AccountCircleIcon sx={{ fontsize: "10rem",textAlign:"center",ml:3 }} />
            <Typography
            marginTop={1}
              padding={1}
              width={"auto"}
              textAlign={"center"}
              border={"1px solid #ccc"}
              borderRadius={6}
            >
              Name: {bookings[0].user.name}
            </Typography>
          </Box>
          <Box width={"70%"} display='flex' flexDirection={"column"}>
            <Typography variant="h3" fontFamily={"verdana"} textAlign={"center"} padding={2}>
              Bookings
            </Typography>
            <Box margin={"auto"} display={"flex"} flexDirection={"column"} width={"80%"}>
              <List>
                {bookings.map((booking, index)=>
                (<ListItem 
                sx={{
                  bgcolor:"#00d386", 
                  color:"white",
                  textAlign:"center", 
                  margin:1,
                }}>
                  <ListItemText sx={{margin:1, width:"auto", textAlign:"left"}} >
                    Movie: {booking.movie.title}
                  </ListItemText>
                  <ListItemText sx={{margin:1, width:"auto", textAlign:"left"}} >
                    Seat: {booking.seatNumber}
                  </ListItemText>
                  <ListItemText sx={{margin:1, width:"auto", textAlign:"left"}} >
                    Date: {new Date(booking.releaseDate).toDateString()}
                  </ListItemText>
                  <DeleteIcon />
                </ListItem>)
              )
            }
              </List>
            </Box>
          </Box>
        </Fragment>
      )}
    </Box>
  );
};

export default UserProfile;
