// HomePage.js

import React, { useEffect, useState } from "react";
import { Box, Button, Typography } from "@mui/material";
import MovieItem from "./Movies/MovieItem";
import { Link } from "react-router-dom";
import { getAllMovies } from "../api-helpers/api-helpers";

const HomePage = () => {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        getAllMovies()
            .then(data => setMovies(data.movies))
            .catch(err => console.log(err));
    }, []);

    return (
        <Box width={"100%"} height="100%" margin="auto" marginTop={2}>
            <Box margin={"auto"} width={"80%"} height={"40vh"} padding={2}>
                <img
                    src="https://assets-in.bmscdn.com/discovery-catalog/events/et00384234-mfxnprbaxs-landscape.jpg"
                    alt="Shaitaan"
                    width={"100%"}
                    height={"100%"}
                />
            </Box>
            <Box padding={5} margin={"auto"}>
                <Typography variant="h4" textAlign={"center"}>
                    Latest Releases
                </Typography>
            </Box>
            <Box display="flex" width="80%" justifyContent={"center"} flexWrap={"wrap"}>
                {movies && movies.slice(0, 4).map(movie => (
                    <MovieItem
                        id={movie.id}
                        title={movie.title}
                        posterUrl={movie.posterUrl}
                        releaseDate={movie.releaseDate}
                        key={movie.id}
                    />
                ))}
            </Box>
            <Box display={'flex'} padding={5} margin={'auto'}>
                <Button component={Link} to="/movies" variant='contained' sx={{ margin: 'auto', color: '#2b2d42' }}>
                    View All Movies
                </Button>
            </Box>
        </Box>
    );
};

export default HomePage;
